// 构建与 ai-analysis.js 一致的 Prompt
const alertData = $('Webhook 接收温度报警').item.json.body;
const contextData = $input.item.json;

const roomCode = alertData.roomCode;
const roomName = alertData.roomName;
const temperature = alertData.temperature;
const threshold = alertData.threshold;
const alertType = alertData.alertType || 'high';
const fileId = alertData.fileId;

// 提取上下文
const assets = contextData.assets || [];
const documents = contextData.documents || [];
const fileIds = contextData.fileIds || [];
const kbId = contextData.kbId || null;

// 构建设备列表
let assetList = '（无设备信息）';
if (assets.length > 0) {
  assetList = `### 房间内设备\n${assets.map(a => `- ${a.name} (${a.asset_code}) [${a.category || '其它设备'}]`).join('\n')}`;
}

// 构建文档列表
let docList = '（无相关文档）';
if (documents.length > 0) {
  docList = documents.map(d => `- ${d.file_name}`).join('\n');
}

const prompt = `你是一个建筑设施运维专家。请根据以下报警信息和上下文，提供运维建议。

**重要规则**：
1. **全程必须使用中文回答**。
2. **不要**输出你的思考过程、任务复述或英文摘要。
3. **不要**使用英文标题，必须严格按照下方的【输出格式】回答。

## 报警信息
- 房间：${roomName} (${roomCode})
- 当前温度：${temperature}°C
- 报警阈值：${threshold}°C
- 报警类型：${alertType === 'high' ? '高温报警' : '低温报警'}

## 上下文信息
${assetList}

## 可用参考文档
${docList}

## 【输出格式】
请严格按照以下格式输出，不同层级使用不同编号样式和缩进：

### 1. 可能原因分析
（一级标题使用"### 数字."格式）
  1) 二级条目使用"数字)"格式，缩进2空格
    - 三级细节使用"- "格式，缩进4空格

### 2. 建议的处理步骤
  1) 第一步操作说明
    - 具体操作细节
    - 注意事项
  2) 第二步操作说明
    - 具体操作细节

### 3. 需要检查的设备
  1) 设备类型一
    - 设备名称 (编码)
  2) 设备类型二
    - 设备名称 (编码)

**注意**：请不要输出"参考的文档"部分，系统会自动根据你的引用生成。在正文中使用 [N] 格式引用文档即可（N为数字）。`;

return {
  prompt,
  kbId,
  fileIds,
  alertData,
  documents,
  roomCode,
  roomName,
  temperature,
  threshold,
  alertType
};

// 解析 AI 回复
const response = $input.item.json;
const prevData = $('构建 RAG Prompt').item.json;

// 提取分析文本
let analysisText = '';
if (response.choices && response.choices.length > 0) {
  analysisText = response.choices[0].message?.content || '';
} else if (response.message?.content) {
  analysisText = response.message.content;
}

// 提取 sources（供后端格式化使用）
const sources = response.sources || [];

// 构建 sourceIndexMap
const sourceIndexMap = {};
for (let i = 0; i < sources.length; i++) {
  const sourceItem = sources[i];
  const sourceIndex = i + 1;
  const openwebuiFileId = sourceItem.source?.id || sourceItem.metadata?.[0]?.file_id;
  const docName = sourceItem.metadata?.[0]?.name || sourceItem.metadata?.[0]?.source || `Source ${sourceIndex}`;

  if (openwebuiFileId) {
    sourceIndexMap[String(sourceIndex)] = {
      index: sourceIndex,
      openwebuiFileId,
      name: docName
    };
  }
}

// 返回结果（来源格式化将在后端完成）
return {
  analysis: analysisText,
  rawSources: sources,
  sourceIndexMap,
  alert: {
    roomCode: prevData.roomCode,
    roomName: prevData.roomName,
    temperature: prevData.temperature,
    threshold: prevData.threshold,
    alertType: prevData.alertType
  }
};
