parameters.jsCode:
// 解析 Gemini API 响应并组装最终结果
const alertData = $('Parse Alert Data').first().json;
const geminiResponse = $input.first().json;

// 提取 Gemini 返回的文本
let analysisText = '';
try {
  if (geminiResponse.candidates && geminiResponse.candidates[0]) {
    const content = geminiResponse.candidates[0].content;
    if (content && content.parts && content.parts[0]) {
      analysisText = content.parts[0].text;
    }
  }
} catch (e) {
  analysisText = '分析失败: ' + e.message;
}

return [{
  json: {
    success: true,
    alert: alertData,
    analysis: analysisText,
    processedAt: new Date().toISOString()
  }
}];
