// 解析报警数据
const alertData = $input.first().json;
const data = alertData.data || alertData;

// 格式化输出
return [{
  json: {
    roomCode: data.roomCode || 'UNKNOWN',
    roomName: data.roomName || data.roomCode || '未知房间',
    temperature: data.temperature || 0,
    threshold: data.threshold || 28,
    severity: data.severity || (data.temperature >= (data.threshold || 28) + 5 ? 'critical' : 'warning'),
    timestamp: data.timestamp || new Date().toISOString(),
    fileId: data.fileId,
    message: `房间 ${data.roomName || data.roomCode || '未知'} 温度报警: ${data.temperature}°C (阈值: ${data.threshold || 28}°C)`
  }
}];

